import React, { useEffect, useState } from 'react';
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';
import { Buffer } from 'buffer';
import logo from '../assets/icon.png'
import { currencyFormatter } from '../helpers/utils';
import { baseUrl } from '../helpers/https';
import Swal from 'sweetalert2';
import axios from 'axios';
import { hideLoader, showLoader } from '../utils/loader';
import { Loader2 } from 'lucide-react';

const RoomTypeSelect = ({ roomType, setRoomType, rooms, checkIn, checkOut }) => {
 

  const roomTypes = [
    { value: 'MOROCCO', label: 'morocco', price: 45000 },
    { value: 'Deluxe Suite 1', label: 'Deluxe Suite 1', price: 45000 },
    { value: 'Deluxe Suite 2', label: 'Deluxe Suite 2', price: 45000 },
    { value: 'Deluxe Suite 3', label: 'Deluxe Suite 3', price: 45000 },
    { value: 'King Suite', label: 'King Suite', price: 55000 },
  ];

  const handleAvailabilityCheck = async(roomType) => {
    if (!checkIn || !checkOut) {
      Swal.fire('Checkin and Checkout dates must be selected!')
      return false;
    }
      // Swal.fire('Checking room availalability')
    try {
        showLoader();
        const response = await fetch(`${baseUrl}/rooms/availability/${roomType}/${checkIn}/${checkOut}`);
        if (!response.ok) {
          hideLoader();
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        let avail = false;
        const res = await response.json();
        console.log({res})
    if (res.status){
      avail = res?.isAvalable;
    }
    return avail;
    } catch (error) {
        console.error('Error fetching/saving payments:', error);
    } finally {
      hideLoader();
    }
  }
  const handleChange = async(e) => {
    const { name, value, checked } = e.target;
  //   setRoomType(prevRoomType => ({
  //     ...prevRoomType,
  //     [name]: type === 'checkbox' ? (checked ? [...prevRoomType[name], value] : prevRoomType[name].filter(item => item !== value)) : value
  // }));
  // const avail = await handleAvailabilityCheck(value);
  // if (!avail) {
  //   return Swal.fire(`${value} is not available for the dates selected!` );
  // }
  setRoomType(prevRoomType => checked ? [...prevRoomType, value] : prevRoomType.filter(item => item !== value));
};
  return (
    <div className="mb-4">
      <label htmlFor="roomType" className="block text-gray-700 text-sm font-bold mb-2">Room Type:</label>
      {rooms.map((room) => (
        <label>
        <input className='mx-3' type="checkbox" name="roomType" value={room?.name}
          checked={roomType.includes(room?.name)}
          onChange={handleChange}
          // disabled={roomName !== null && roomName !== room?.name}
        />
          {`${room?.name} - ${currencyFormatter(room?.price)}`}
          <br/>
        </label>
        ))}
    </div>
  );
};

const Form = () => {
  // State for managing invoice data
  const [invoiceNumber, setInvoiceNumber] = useState(Date.now());
  const [guestName, setGuestName] = useState('');
  const [address, setAddress] = useState('');
  const [contact, setContact] = useState('');
  const [roomType, setRoomType] = useState([]);
  const [guest, setGuest] = useState(1);
  const [rooms, setRooms] = useState([]);
  const [online, setOnline] = useState(navigator.onLine);
  const [checkIn, setCheckIn] = useState('');
  const [checkOut, setCheckOut] = useState('');
  const [email, setEmail] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isLoadingRooms, setIsLoadingRooms] = useState(false);

  useEffect(() => {
          if (!checkIn || !checkOut) return;
          const fetchRooms = async () => {
              try {
                setIsLoadingRooms(true);
                  const response = await fetch(`${baseUrl}/rooms/${checkIn}/${checkOut}`);
                  const data = await response.json();
                  setRooms(data.data);
                  // setPendinPay(data.pendPayment);
                  console.log({ data });
              } catch (error) {
                  console.error('Error fetching rooms:', error);
              } finally {
                  hideLoader();
                  setIsLoadingRooms(false);
              }
          };
  
          fetchRooms();
      }, [checkIn, checkOut]);
  useEffect(() => {
      function handleOnline() {
          setOnline(true);
          // synchronizeData()
      }
      function handleOffline() {
          setOnline(false);
          // fetchOfflineRooms()
      }

      window.addEventListener('online', handleOnline);
      window.addEventListener('offline', handleOffline);

      if (online) {
          // synchronizeData()
      } else {
          // fetchOfflineRooms()
      }

      return () => {
          window.removeEventListener('online', handleOnline);
          window.removeEventListener('offline', handleOffline);
      };
  }, [online]);

  const synchronizeData = async () => {
      try {
          const response = await fetch(`${baseUrl}/rooms`);
          const onlineRooms = await response.json();
          setRooms(onlineRooms);
          const result = await window.electronAPI.saveRoomsOffline(onlineRooms)
          if(!result.success) console.error(result.error)
      } catch (error) {
          console.error('Error fetching rooms:', error);
          // fetchOfflineRooms()
      }
  }

  const fetchOfflineRooms = async () => {
      try {
          const rooms = await window.electronAPI.getRoomsOffline()
          console.log('offf', rooms);
          setRooms(rooms)
      } catch (error) {
          console.error("Error fetching rooms offline:", error);
      }
  }

  // Helper function to find room price
  const getRoomPrice = () => {
    const totalAmount = rooms.filter(room => roomType.includes(room.name)).reduce((acc, room) => acc + room.price, 0);
    return totalAmount;
    // const room = ['Standard Suite', 'Deluxe Suite 1', 'Suite'].find(r => r === roomType);
    // return room === 'Standard Suite' ? 40000 
    // : (room === 'Deluxe Suite 1' || room === 'Deluxe Suite 2' ||room === 'Deluxe Suite 3' )
    // ? 45000 : 55000;
  };

  const getPerRoomPrice = (room) => {
    const price = rooms.find(r => r.name === room);
    return price ? price.price : 0;
  };

  // Calculate totals
  const nights = Math.ceil((new Date(checkOut) - new Date(checkIn)) / (1000 * 60 * 60 * 24));

  const roomCost = (getRoomPrice() * nights);
  const cautionFee = roomType.length * 20000;
  let discount =0;
  // if(nights > 3 || roomType.length > 3){
  //   discount = roomCost * 0.2;
  // }
  const total = roomCost + cautionFee;

  const generatePDF = async () => {
        const doc = new jsPDF();
        const image = "data:image/jpeg;base64,";
        doc.setFontSize(20);
        doc.text(50, 20, 'Tranquility Lodge');
        doc.addImage(image, 'PNG', 0, 0, 40, 40);
        doc.setFontSize(14);
        doc.text(`Invoice #${Date.now()}`, 10, 60);
        doc.text(`Date: ${ new Date().toISOString().split('T')[0]}`, 10, 70);
        doc.text(`Customer: ${guestName}`, 10, 80);
        doc.text(`Phone: ${contact }`, 10, 90);
        doc.text(`Address: ${address }`, 10, 100);  
        
        doc.text(`Check-in Date: ${new Date(checkIn).toISOString().split('T')[0] }`, 10, 115);
        doc.text(`Check-out Date: ${new Date(checkOut).toISOString().split('T')[0] }`, 10, 125);

        const tableColumn = ["Room",  "Price"];
        const tableRows = await roomType.map(item => [item, getPerRoomPrice(item)]);
        
        doc.autoTable({ head: [tableColumn], body: tableRows, startY: 135 });

        // const total = calculateTotal();
        doc.text(`Subtotal: ${currencyFormatter(roomCost)}`, 10, doc.lastAutoTable.finalY + 10);
        doc.text(`Caution Fee (Refundable): ${currencyFormatter(cautionFee)}`, 10, doc.lastAutoTable.finalY + 20);
        // doc.text(`Discount: ${(discount)}`, 10, doc.lastAutoTable.finalY + 30);
        doc.text(`Total: ${currencyFormatter(total)}`, 10, doc.lastAutoTable.finalY + 40);

        doc.text(`Thanks for your patronage`, 10, doc.lastAutoTable.finalY + 50);

        // doc.save(`invoice_${Date.now()}.pdf`);
        const pdfBytes = doc.output('arraybuffer');
        const pdfBase64 = await Buffer.from(pdfBytes).toString('base64');
        console.log('base',pdfBase64);
        return { filename: `invoice_.pdf`, content: pdfBase64}
    };

    const sendEmail = async () => {
      const pdfAttachment = generatePDF()
      const emailData = {
          to: 'kabirnajib0@gmail.com', // Get recipient email from input field or state
          subject: `Invoice #${invoiceNumber}`,
          html: `<p>Please find attached your invoice.</p>`, // Customize email body
          attachments: [{
              filename: pdfAttachment.filename,
              content: pdfAttachment.content,
              encoding: 'base64'
          }]
      };

      const result = await window.electronAPI.sendInvoiceEmail(emailData);

      if (result.success) {
          alert('Invoice sent successfully!');
      } else {
          alert(`Error sending invoice: ${result.message}`);
      }
  };

  const handleBooking = async () => {
    if (!roomType.length || !checkIn || !checkOut || !guestName || !email || !contact) {
      Swal.fire('Error', 'Please fill in all fields', 'error');
      return;
    }
    const formData =
    {
        roomTypes: roomType,
        checkIn,
        checkOut,
        guests: guest,
        name: guestName,
        email,
        phone: contact
      
     }
    try{
      setIsLoading(true)
      // showLoader();
    const response = await axios.post(`${baseUrl}/booking`, formData)
    const totalAmount = rooms.filter(room => roomType.includes(room.name)).reduce((acc, room) => acc + room.price, 0);
    // const res = response.json()
    if (response?.data?.status && response?.data?.data[0]?.booking_id) {
      Swal.fire('Successful', `Invoice and payment link has been sent to your ${email}`, 'Done')
      setRoomType([]);
      setCheckIn('');
      setCheckOut('');
      setGuestName('');
      setEmail('');
      setContact('');
      setRooms([]);
      // navigate(`/payment/${res?.data[0]?.booking_id}`, { state: { bookingData: {...formData, booking_id: res?.data[0]?.booking_id} , totalAmount: totalAmount } });
    }
    setIsLoading(false)
  }catch(err){
    console.log(err)
    setIsLoading(false)
  } finally{
    hideLoader();
    setIsLoading(false)
  }
  }
  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-bold">Tranquilty Lodge Invoice</h1>
        <img src={logo} style={{ height: 16, width: 16}} alt="lodge Logo" className="img-fluid h-16 w-16" />
      </div>

      <div className="bg-white p-6 rounded-lg shadow-md">
        {/* Invoice Number */}
        <div className="mb-4">
          <label htmlFor="invoice-number" className="block text-gray-700 text-sm font-bold mb-2">Invoice Number:</label>
          <input 
            type="text" 
            id="invoice-number" 
            className="w-full p-2 border rounded-md" 
            value={invoiceNumber} 
            onChange={(e) => setInvoiceNumber(e.target.value)} 
            // placeholder="INV-001" 
            disabled
          />
        </div>

        {/* Date */}
        <div className="mb-4">
          <div className="form-group">
            <h2 className='text-lg font-semibold mb-2' htmlFor="checkIn">Check-in Date:</h2>
            <input
                type="date"
                id="checkIn"
                name="checkIn"
                className='w-full p-2 border rounded-md'
                value={checkIn}
                onChange={(e) => {
                  setCheckIn(e.target.value)
                  setCheckOut('');
                  setRooms([]);
                  setRoomType([]);
                }}
                required
                min={new Date().toISOString().split('T')[0]} // Disable past dates
                // disabled
            />
        </div>
        <div className="mb-4">
            <h2 className='text-lg font-semibold mb-2' htmlFor="checkOut">Check-out Date:</h2>
            <input
                type="date"
                id="checkOut"
                name="checkOut"
                className='w-full p-2 border rounded-md'
                value={checkOut}
                onChange={(e) => {
                  setCheckOut(e.target.value)
                  setRoomType([]);
                  setRooms([]);
                }}
                required
                min={checkIn || new Date().toISOString().split('T')[0]}
                // disabled
            />
        </div>
          {/* <input type="text" id="date" className="w-full p-2 border rounded-md" defaultValue={new Date().toLocaleDateString()} readOnly /> */}
        </div>

        {/* Customer Information */}
        <div className="mb-4">
          <h2 className="text-lg font-semibold mb-2">Customer Information</h2>
          <input type="text" className="w-full p-2 border rounded-md mb-2" placeholder="Guest Name" value={guestName} onChange={(e) => setGuestName(e.target.value)} />
          <input type="text" className="w-full p-2 border rounded-md mb-2" placeholder="Address" value={address} onChange={(e) => setAddress(e.target.value)} />
          <input type="email" className="w-full p-2 border rounded-md" placeholder="email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <input type="text" className="w-full p-2 border rounded-md" placeholder="08012345678" value={contact} onChange={(e) => setContact(e.target.value)} />
        </div>

        {/* Room and Charges */}
        <div className="mb-4">
          <h2 className="text-lg font-semibold mb-2">Room and Charges</h2>
           {isLoadingRooms ? (
              <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" /> Fetching available rooms...
              </>
              ) : (
          <RoomTypeSelect roomType={roomType} setRoomType={setRoomType} rooms={rooms} checkIn={checkIn} checkOut={checkOut}/>
              )}
          <h4 className="text-lg font-semibold mb-2">Number of Guest</h4>
          <input
            type="number"
            min={1}
            value={guest}
            onChange={(e) => setGuest(e.target.value)}          
          className="w-full p-2 border rounded-md" placeholder="Number of Guest" 
          />
        </div>

        {/* Totals */}
        <div className="mb-4">
          <div className="grid grid-cols-2 gap-4">
            <div className="text-right font-bold">Subtotal:</div>
            <div className="text-right">{currencyFormatter(roomCost ?? 0)}</div>
            <div className="text-right font-bold">Caution Fee (Refundable)</div>
            <div className="text-right">{currencyFormatter(cautionFee)}</div>
            <div className="text-right font-bold">No of Nights:</div>
            <div className="text-right">{Math.ceil((new Date(checkOut) - new Date(checkIn))/(1000 * 60 * 60 *24))}</div>
            {/* <div className="text-right font-bold">Discount</div>
            <div className="text-right">{currencyFormatter(discount)}</div> */}
            <div className="text-right font-bold">Total:</div>
            <div className="text-right text-xl font-bold">{currencyFormatter(total ?? 0)}</div>
          </div>
        </div>
        {/* <div className="mt-6">
            <button onClick={generatePDF} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Generate PDF</button>
        </div> */}
        {/*<div className="mt-6">
            <button onClick={sendEmail} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Send Email PDF</button>
        </div> */}
        <div className="mt-6">
            <button disabled={isLoading} onClick={handleBooking} className="bg-[#ff6700] hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
               {isLoading ? (
                  <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" /> Submitting...
                  </>
                  ) : (
              'Submit Booking'
                  )}
              
            </button>
        </div> 
        <div className="text-sm text-gray-600">
          {/* <p>Thank you for choosing Hotel XYZ. Please review the charges above. Payments can be made via credit card at the front desk or online at www.hotelxyz.com/pay.</p> */}
        </div>
      </div>
    </div>
  );
};

export default Form;